mapper.select:
SELECT ARG_NAME, ARG_TYPE, ARG_VALUE, ALLOCATED FROM ( SELECT t.*, 1 AS ALLOCATED FROM ${tablePrefix}arg t WHERE ARG_NAME IN ( SELECT ar.ARG_NAME FROM ${tablePrefix}arg ar JOIN ${tablePrefix}job_arg j ON ar.ARG_NAME = j.ARG_NAME
) UNION ALL SELECT t.*, 0 AS ALLOCATED FROM ${tablePrefix}arg t WHERE ARG_NAME NOT IN ( SELECT ar.ARG_NAME FROM ${tablePrefix}arg ar JOIN ${tablePrefix}job_arg j ON ar.ARG_NAME = j.ARG_NAME
) ) a
AND LOWER(ARG_NAME) LIKE CONCAT(CONCAT('%', LOWER(#{argName, jdbcType=VARCHAR})), '%')
AND ARG_TYPE = #{argType, jdbcType = INTEGER}
AND LOWER(ARG_VALUE) LIKE CONCAT(CONCAT('%', LOWER(#{argValue, jdbcType = VARCHAR})), '%')

mapper.insert:
INSERT INTO ${tablePrefix}job_arg (
    JOB_NAME,
    ARG_NAME
  )
  VALUES
    (
      #{jobName, jdbcType=VARCHAR},
      #{argName, jdbcType=VARCHAR}
    )

INSERT INTO ${tablePrefix}job_arg (
JOB_NAME,
ARG_NAME
)
VALUES

    (
    #{jobName, jdbcType=VARCHAR},
    #{argName, jdbcType=VARCHAR}
    )

INSERT INTO ${tablePrefix}job_arg ( JOB_NAME, ARG_NAME ) SELECT * FROM (
SELECT #{jobName, jdbcType=VARCHAR}, #{argName, jdbcType=VARCHAR} FROM dual
)

mapper.delete:
DELETE FROM ${tablePrefix}job_arg



        AND ARG_NAME in

            #{argName, jdbcType=VARCHAR}
